INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Armored Brown Bear', 'Your cries of "For the Horde!" will be much more menacing from atop a heavily armored bear that''s barreling down on Alliance at top speed.', 'Vendor', 'Horde', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-27821.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Armored Brown Bear', 'This ursine mount''s swiftness is enhanced by its heavy armor, imbued with magic from Kirin Tor magi.', 'Vendor', 'Alliance', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-27820.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Black War Bear', 'This formidable mount is bought with the blood of Alliance leaders, but you would have killed them for free.', 'Achievement', 'Horde', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-27819.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Black War Bear', 'Killing leaders of the Horde is its own reward, but this intimidating beast is an excellent memento.', 'Achievement', 'Alliance', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-27818.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Mekgineer''s Chopper', 'Sporting superb engineering craftsmanship, this chopper is one of the few personal vehicles on Azeroth built for two.', 'Profession', 'Alliance', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-25870.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Armored Snowy Gryphon', 'The nice thing is that when they attack, blood almost never stains their feathers. -Mei Francis', 'Vendor', 'Alliance', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-27913.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Armored Blue Wind Rider', 'They are not native to Northrend, but they have adapted extremely well to the cold. -Mei Francis', 'Vendor', 'Horde', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-27914.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Traveler''s Tundra Mammoth', 'Ancient manuscripts depict merchants traveling across the frozen tundra with these colossal beasts, leading scholars to believe they are the first domesticated mounts.', 'Vendor', 'Alliance', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-27237.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Traveler''s Tundra Mammoth', 'Riders and merchants alike trust only this strong and steadfast mammoth to carry them safely across the frigid tundra.', 'Vendor', 'Horde', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-27238.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Grand Black War Mammoth', 'Once used by giants to carry heavily armored warriors, these grand war mammoths can easily bear more than one rider of less-than-giant stature.', 'Drop', 'Alliance', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-27241.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Grand Black War Mammoth', 'Rarely, a war mammoth is born stronger and more tractable than its peers. These mammoths are dubbed "grand" and carry more than one rider to the front lines.', 'Drop', 'Horde', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-27240.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Grand Ice Mammoth', 'At home on the narrow, icy trails of the Storm Peaks, these mammoths are the only mounts the ice giants trust to carry large loads and multiple riders.', 'Vendor', 'Horde', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-27239.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Grand Ice Mammoth', 'Due to high winds and avalanches, the Sons of Hodir recommend bringing at least two companions if venturing into the dangerous Storm Peaks.', 'Vendor', 'Alliance', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-27242.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Blue Dragonhawk', 'Dragonhawks are among the deadliest known predators. They are able to spot prey from great distances, rush in, and rend with both talon and beak.', 'Achievement', 'Alliance', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-27525.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Red Dragonhawk', 'Dragonhawks are among the deadliest known predators. They are able to spot prey from great distances, rush in, and rend with both talon and beak.', 'Achievement', 'Horde', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-28402.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Stormwind Steed', 'Although raised in Stormwind, this breed showed unusual aptitude for the cold weather in Icecrown.', 'Vendor', 'Alliance', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-28912.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Darkspear Raptor', 'This raptor breed was originally cultivated by the Drakkari, but has since been adopted by the Darkspear tribe in Northrend.', 'Vendor', 'Horde', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-29261.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Darnassian Nightsaber', 'When a night elf''s mount perishes, it is custom for the rider to keep one of the saber''s great fangs as a token of remembrance.', 'Vendor', 'Alliance', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-29256.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Ironforge Ram', 'Wildhammer Fact Checker claims that this specific breed was the first type of ram domesticated by the dwarves when they settled in Dun Morogh.', 'Vendor', 'Alliance', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-29258.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Gnomeregan Mechanostrider', 'When you absolutely, positively have to be there right now. -Rillie Spindlenut', 'Vendor', 'Alliance', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-28571.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Exodar Elekk', 'Exodar Elekk are elekk which have been anointed by O''ros, the Naaru who journeyed from Draenor to Azeroth with Velen and his followers.', 'Vendor', 'Alliance', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-29255.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Orgrimmar Wolf', 'For a brief time, many orcs would declare themselves "hungry like an Orgrimmar wolf." Some blood elves still use the phrase ironically.', 'Vendor', 'Horde', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-29260.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Forsaken Warhorse', 'When fallen heroes are raised into undeath, so too are their horses.', 'Vendor', 'Horde', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-29257.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Thunder Bluff Kodo', 'An alpha member of the herd, this kodo is a prime example of the mighty creatures which thunder through Mulgore.', 'Vendor', 'Horde', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-29259.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Silvermoon Hawkstrider', 'So I says I feel like chicken tonight. Next thing, I am flat out and that thing is trying to see what my eyes taste like. -Bozzle Blastbolt', 'Vendor', 'Horde', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-29262.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('White Kodo', '"The kodo is the backbone of our people. Wherever the Horde goes - in trade, settlement, or war - there you will find the mighty kodo." - Harb Clawhoof', 'Vendor', 'Horde', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-12241.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Black Wolf', 'Can howl loudly enough to be heard for miles.', 'Vendor', 'Horde', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-207.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Venomhide Ravasaur', 'You raised this thing from a scaly pup - Mor''vek would be so proud. But he''d still make you clean up the mess it made in the corner.', 'Quest', 'Horde', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-29102.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Swift Moonsaber', 'Many night elves revere these proud mounts because their shimmering coats symbolize the goddess of the moon, Elune.', 'Vendor', 'Alliance', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-14333.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Great Red Elekk', 'Never seen in Nagrand, the first red elekk calves were born in Azeroth after the Exodar''s arrival. Some breeders theorize that the unique coloration is somehow caused by the same radiation which ravaged Bloodmyst Isle.', 'Vendor', 'Alliance', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-28606.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Black Skeletal Horse', 'The Forsaken believe that without a purpose, even beasts of burden suffer in undeath.', 'Vendor', 'Horde', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-29130.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Swift Red Hawkstrider', 'What do you mean is it dyed? Oh, the bird. We did touch up the red just a little. -Trellis Morningsun', 'Vendor', 'Horde', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-28607.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Swift Gray Steed', 'A veteran of some of the toughest battles fought by the Argent Crusade.', 'Vendor', 'Alliance', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-29043.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Great Golden Kodo', 'An alpha member of the herd, this kodo is a prime example of the mighty creatures which thunder through the Southern Barrens.', 'Vendor', 'Horde', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-28556.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Swift Violet Ram', 'The Barak Tor''ol ram''s thick bony skull and massive horns make it the ideal mount for charging through groups of armored enemies.', 'Vendor', 'Alliance', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-28612.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Turbostrider', 'The mirror''s not so you can look back, but so your foes can see something besides your tailpipes. -Rillie Spindlenut', 'Vendor', 'Alliance', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-14375.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Swift Purple Raptor', 'Longtime allies of the trolls, these jungle hunters form a special bond with their masters. They become mount, guardian, and friend.', 'Vendor', 'Horde', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-14343.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('White Skeletal Warhorse', 'When fallen heroes are raised into undeath, so too are their horses.', 'Vendor', 'Horde', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-28605.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Swift Burgundy Wolf', 'The wolves of the Horde are befriended, not domesticated.', 'Vendor', 'Horde', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-14335.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Sunreaver Hawkstrider', 'I demand speed, grace and discipline on the battlefield. This one will serve nicely. -Eressea Dawnsinger', 'Vendor', 'Horde', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-28889.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Silver Covenant Hippogryph', 'Silver Covenant Hippogryphs are fiercely loyal, both to their riding partners, and to the Alliance.', 'Vendor', 'Alliance', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-22474.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Quel''dorei Steed', 'Comes from a lineage of fine horses that once grazed near the shores of the Well of Eternity.', 'Vendor', 'Alliance', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-28888.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Sunreaver Dragonhawk', 'The Sunreavers of Dalaran select only the swiftest and most formidable of dragonhawks as their mounts.', 'Vendor', 'Horde', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-29696.jpg');
