INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Swift Alliance Steed', 'A few of the Argent Crusade''s swiftest mounts were awarded to those members of the Alliance who performed exceptionally during the tournament in Icecrown.', 'Vendor', 'Alliance', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-29284.jpg');
