INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Crusader''s White Warhorse', 'Awarded to only the most skilled champions from the Trial of the Crusader, these steeds were chosen for Alliance members from Tirion Fordring''s own personal stable.', 'Achievement', 'Alliance', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-29937.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Crusader''s Black Warhorse', 'Awarded to only the most skilled champions from the Trial of the Crusader, these steeds were chosen for Horde members from Tirion Fordring''s own personal stable.', 'Achievement', 'Horde', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-29938.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Sunwalker Kodo', 'This magnificent beast, adorned in sacred charms and painted with holy imagery, radiates with the warmth of the sun.', 'Quest', 'Horde', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-30366.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Great Sunwalker Kodo', 'At daybreak, the noble Sunwalkers often perform rituals to bless their mounts, imbuing them with holy magic.', 'Quest', 'Horde', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-30501.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Goblin Trike', 'A compilation of nuts, bolts, gears, and treated wood paneling make for an unusually smooth ride.', 'Vendor', 'Horde', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-35249.jpg');
INSERT INTO Mounts (name, description, source, faction, media)VALUES ('Exarch''s Elekk', 'Draenei exarchs have long marched into war atop these imposing mounts, bred for their razor-sharp tusks.', 'Quest', 'Alliance', 'https://render-us.worldofwarcraft.com/npcs/zoom/creature-display-31367.jpg');
